import { LOG_IN_REQUEST,
    LOG_IN_SUCCESS,
    LOG_IN_ERROR,
    LOG_OUT_REQUEST,
    LOG_OUT_SUCCESS } from 'actionTypes'

import { fetchUser } from 'api/index'


export function logInRequest (email, password) {
    console.log("action");
    const user = fetchUser(email, password);
    console.log(user);
    console.log(LOG_IN_REQUEST);
    return {type: LOG_IN_REQUEST,  payload: user};
}

export function logInSuccess (user) {
    return { type: LOG_IN_SUCCESS, payload: user };
}
export function logInError () {
    return { type: LOG_IN_ERROR };
}
export function logOutRequest () {
    return { type: LOG_OUT_REQUEST };
}
export function logOutSuccess () {
    return { type: LOG_OUT_SUCCESS };
}