import { REGISTRATION_REQUEST,
    REGISTRATION_SUCCESS,
    REGISTRATION_ERROR } from 'actionTypes'

import {addUser} from 'api/index'

export function registrationRequest (name, surname, phone, email, password) {
    const user = addUser(name, surname, phone, email, password);
    return { type: REGISTRATION_REQUEST, payload: user };
}
export function registrationSuccess () {
    return { type: REGISTRATION_SUCCESS };
}
export function registrationError () {
    return { type: REGISTRATION_ERROR };
}