import React, {Component} from 'react';
import './navbar.css';
import {getUserIsAdmin} from "userSelector";
import {connect} from "react-redux";

class addBookButton extends Component {
    constructor(props) {
        super(props);
    }
    render() {
        return (
            <div className="add-book" style={{display: this.props.show ? 'block': 'none'}}>
                <a href="/admin">Add book</a>
            </div>
        );
    }
}

export  default connect(
    state => ({
        show: getUserIsAdmin(state)
    }),
)(addBookButton);
